#!/usr/bin/env sh

# Synopsis:
# Test the analyzer by running it against a predefined set of solutions 
# with an expected output.

# Output:
# Outputs the diff of the expected analysis results against the actual analysis results
# generated by the analyzer.

# Example:
# ./bin/run-tests.sh

exit_code=0

# Iterate over all test directories
for test_dir in $(find tests -name expected_analysis.json | rev | cut -d '/' -f 2- | rev); do
    test_dir_name=$(basename "${test_dir}")
    test_dir_path=$(realpath "${test_dir}")
    results_file_path="${test_dir_path}/analysis.json"
    expected_results_file_path="${test_dir_path}/expected_analysis.json"
    test_slug=$(echo "${test_dir}" | awk -F/ '{ print $2 }')

    bin/run.sh "${test_slug}" "${test_dir_path}" "${test_dir_path}"

    for file in analysis.json tags.json; do
        expected_file="expected_${file}"
        echo "${test_dir_name}: comparing ${file} to ${expected_file}"

        if ! diff "${test_dir_path}/${file}" "${test_dir_path}/${expected_file}"; then
            exit_code=1
        fi
    done
done

exit ${exit_code}
